Prompt Post-Load SQL T_META_BATCH_TASK_TABLE_X_CFG
prompt merge to t_meta_batch_task_table_x

delete from g77_cfg.t_meta_batch_task_table_x target
where not exists (select 1 from g77_cfg.t_meta_batch_task_table_x_cfg cfg where target.batch_task_id = cfg.batch_task_id and target.table_id = cfg.table_id and target.object_role = cfg.object_role);


merge
 into
   g77_cfg.t_meta_batch_task_table_x target
   using
     ( select
            cfg.batch_task_id
          , cfg.table_id
          , cfg.object_role
          , cfg.dq_checks
          , cfg.processing_order
          , cfg.restart_mode
          , cfg.event_status_mode
          , cfg.processed_rec_sql
          , cfg.update_processed_sql
          , cfg.gather_stats
          , cfg.active_indicator
         from g77_cfg.t_meta_batch_task_table_x_cfg cfg
         inner join g77_cfg.t_meta_batch_task_table_x target
         on target.batch_task_id = cfg.batch_task_id and target.table_id = cfg.table_id and target.object_role = cfg.object_role
         and (
            nvl(target.object_role, '~') <> nvl(cfg.object_role, '~') or
            nvl(target.dq_checks, '~') <> nvl(cfg.dq_checks, '~') or
            nvl(target.processing_order, -1) <> nvl(cfg.processing_order, -1) or
            nvl(target.restart_mode, '~') <> nvl(cfg.restart_mode, '~') or
            nvl(target.event_status_mode, '~') <> nvl(cfg.event_status_mode, '~') or
            nvl(target.processed_rec_sql, '~') <> nvl(cfg.processed_rec_sql, '~') or
            nvl(target.update_processed_sql, '~') <> nvl(cfg.update_processed_sql, '~') or
            nvl(target.gather_stats, '~') <> nvl(cfg.gather_stats, '~') or
            nvl(target.active_indicator, '~') <> nvl(cfg.active_indicator, '~')
              )
        union all
         select
            cfg.batch_task_id
          , cfg.table_id
          , cfg.object_role
          , cfg.dq_checks
          , cfg.processing_order
          , cfg.restart_mode
          , cfg.event_status_mode
          , cfg.processed_rec_sql
          , cfg.update_processed_sql
          , cfg.gather_stats
          , cfg.active_indicator
         from g77_cfg.t_meta_batch_task_table_x_cfg cfg
         where not exists (select 1 from g77_cfg.t_meta_batch_task_table_x target where target.batch_task_id = cfg.batch_task_id and target.table_id = cfg.table_id and target.object_role = cfg.object_role )
           and batch_task_id is not null
           and table_id is not null
         ) cfg
  on ( target.batch_task_id = cfg.batch_task_id and target.table_id = cfg.table_id and target.object_role = cfg.object_role )
  when matched
  then
    update set
            target.dq_checks         = cfg.dq_checks
          , target.processing_order  = cfg.processing_order
          , target.restart_mode      = cfg.restart_mode
          , target.event_status_mode = cfg.event_status_mode
          , target.processed_rec_sql = cfg.processed_rec_sql
          , target.update_processed_sql = cfg.update_processed_sql
          , target.gather_stats = cfg.gather_stats
          , target.active_indicator  = cfg.active_indicator

    when not matched then insert
    (       target.batch_task_id
          , target.table_id
          , target.object_role
          , target.dq_checks
          , target.processing_order
          , target.restart_mode
          , target.event_status_mode
          , target.processed_rec_sql
          , target.update_processed_sql
          , target.gather_stats
          , target.active_indicator
      ) values
         (
            cfg.batch_task_id
          , cfg.table_id
          , cfg.object_role
          , cfg.dq_checks
          , cfg.processing_order
          , cfg.restart_mode
          , cfg.event_status_mode
          , cfg.processed_rec_sql
          , cfg.update_processed_sql
          , cfg.gather_stats
          , cfg.active_indicator
         );
